import React from 'react';
import './App.css';
import {
  HashRouter as Router,
  Route,
  NavLink
} from 'react-router-dom';

// Install the socket io client using:
//    npm install socket.io
//
// Then import socket io and create a socket:
//
import io from 'socket.io-client';
const socket = io('http://localhost:3001');
//
// See: https://socket.io/get-started/chat
//      https://www.npmjs.com/package/socket.io-client




class Hpage extends React.Component{

  render(){
    return <h1>This is Hoempage</h1>
  }
}

class Page1 extends React.Component{

  constructor(props){
    super(props)
    this.state={low: true, medium: true, high: true, critical:true};
  }

  filterLow(){this.setState({low:!this.state.low});}
  filterMedium(){this.setState({medium:!this.state.medium});}
  filterHigh(){this.setState({high:!this.state.high});}
  filterCritical(){this.setState({critical:!this.state.critical});}


  render(){

    var toshow = this.props.posts;
    if(!this.state.low) toshow = toshow.filter(x => x.priority !== "Low");
    if(!this.state.medium) toshow = toshow.filter(x => x.priority !== "Medium");
    if(!this.state.high) toshow = toshow.filter(x => x.priority !== "High");
    if(!this.state.critical) toshow = toshow.filter(x => x.priority !== "Critical");



    return(
      <div>
        <input type = "checkbox" id="low" checked={this.state.low}
            onChange={this.filterLow.bind(this)}/>
        <label for="low">Low</label>

        <input type = "checkbox" id="medium" checked={this.state.medium}
            onChange={this.filterMedium.bind(this)}/>
        <label for="medium">Medium</label>

        <input type = "checkbox" id="high" checked={this.state.high}
            onChange={this.filterHigh.bind(this)}/>
        <label for="high">High</label>

        <input type = "checkbox" id="critical" checked={this.state.critical}
            onChange={this.filterCritical.bind(this)}/>
        <label for="critical">Critical</label>



        <div>
          {toshow.map(x=><div>{x.priority}</div>)}
          {/* {this.props.posts.map(({priority})=><div>{priority}</div>)} */}
        </div>




        <div>
            {toshow.map( 
              ({name,image,content,problem,priority,id}) => 
              <div 
                key={id}> 
                  <img src={image} alt="{name}" /> <br />
                  {name} <br />
                  {problem} <br />
                  {priority} <br />
                  {content} <br /> <br /> <br /> <br />
              </div> 
            )}
        </div>
      </div>
    )}
}

class Page2 extends React.Component{

  constructor(props){
    super(props)
  }

  render(){

    var FireLow = this.props.posts;
    FireLow = FireLow.filter(x=>x.priority === "Low" && x.problem === "Fire");

    var FloodLow = this.props.posts;
    FloodLow = FloodLow.filter(x=>x.priority === "Low" && x.problem === "Flood");

    var PowerLow = this.props.posts;
    PowerLow = PowerLow.filter(x=>x.priority === "Low" && x.problem === "Power");

    var MedicalLow = this.props.posts;
    MedicalLow = MedicalLow.filter(x=>x.priority === "Low" && x.problem === "Medical");

    var TotalLow = this.props.posts;
    TotalLow = FireLow.length + FloodLow.length + PowerLow.length + MedicalLow.length;

    return(
    <div>
      <table border="1">
        <tr>
         <th></th>
          <th>Fire</th>
          <th>Flood</th>
          <th>Power</th>
          <th>Medical</th>
          <th>Total</th>
        </tr>
       <tr>
          <td>Low</td>
          <td>{FireLow.length}</td>
          <td>{FloodLow.length}</td>
          <td>{PowerLow.length}</td>
          <td>{MedicalLow.length}</td>
          <td>{TotalLow}</td>
        </tr>
        <tr>
          <td>Medium</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>High</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Critical</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
