import React from 'react';
import './App.css';
import {
  HashRouter as Router,
  Route,
  NavLink
} from 'react-router-dom';

// Install the socket io client using:
//    npm install socket.io
//
// Then import socket io and create a socket:
//
import io from 'socket.io-client';
const socket = io('http://localhost:3001');
//
// See: https://socket.io/get-started/chat
//      https://www.npmjs.com/package/socket.io-client




class Hpage extends React.Component{

  render(){
    return <h1>This is Hoempage</h1>
  }
}

class Page1 extends React.Component{

  constructor(props){
    super(props)
    this.state={low: false};
  }

  filterLow(){this.setState({low:!this.state.low});}


  render(){

    var toshow = this.props.posts;
    if(this.state.low) toshow = toshow.filter(x => x.priority === "Low");

    return(
      <div>
        <input type = "checkbox" id="low" checked={this.state.low}
            onChange={this.filterLow.bind(this)}/>
        <label for="low">Low</label>



        <div>
          {toshow.map(x=><div>{x.priority}</div>)}
          {/* {this.props.posts.map(({priority})=><div>{priority}</div>)} */}
        </div>




        <div>
            {toshow.map( 
              ({name,image,content,problem,priority,id}) => 
              <div 
                key={id}> 
                  <img src={image} alt="{name}" /> <br />
                  {name} <br />
                  {problem} <br />
                  {priority} <br />
                  {content} <br /> <br /> <br /> <br />
              </div> 
            )}
        </div>
      </div>
    )}
}

class Page2 extends React.Component{

  constructor(props){
    super(props)
  }

  render(){

    var FireLow = this.props.posts;
    FireLow = FireLow.filter(x=>x.priority === "Low" && x.problem === "Fire");


    return(
    <div>
      <table border="1">
        <tr>
         <th></th>
          <th>Fire</th>
          <th>Flood</th>
          <th>Power</th>
          <th>Medical</th>
          <th>Total</th>
        </tr>
       <tr>
          <td>Low</td>
          <td>{FireLow.length}</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Medium</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>High</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Critical</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Total</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
      </table>
    </div>
    )}
}







class App extends React.Component {

  constructor(props) {
    super(props);

    // An array of social media posts messages, and we'll increment nextID
    // to maintain a unique ID for each post in our array
    this.state = {posts: [],nextID: 0};

    // We can setup the event handlers for the messages in the constructor...
    socket.on('connect', function(){

      console.log("Connect....");

      // When we receive a social media message, call setState and insert 
      // it into the array of posts
      socket.on('post', 

        function(data) {

          // Can uncomment this to see the raw data coming in...
          // console.log("post: " + JSON.stringify(data));

          // increment the next unique ID, and put post data into the list of 
          // posts... use the '...' syntax to make this insertion easier:
          // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/Spread_syntax 
          this.setState( 
            {posts: [...this.state.posts,
                     {name: data.name, 
                      image: data.image, 
                      content: data.content, 
                      problem: data.problem,
                      priority: data.priority,
                      id: this.state.nextID}]
            ,nextID: this.state.nextID + 1} );
        }.bind(this));
        // ^^ Like other event handlers, we bind the callback function to the 
        // component so we can use setState        

    }.bind(this));
    // ^^ ... And same with the callback function for when a connection is made

  }

  // Output all the posts into a table
  render() {
    return (
      <div>
        <Router>
          <NavLink to="/hpage">Home</NavLink>
          <NavLink to="/page1">Page1</NavLink>
          <NavLink to="/page2">Page2</NavLink>

          <Route path="/hpage" component={Hpage}/>
          <Route path="/page1" render = {(props) => <Page1 {...props} posts={this.state.posts} />}/>
          <Route path="/page2" render = {(props) => <Page2 {...props} posts={this.state.posts} />}/>

        </Router>      

      </div>
    );
  }
}





export default App;
