        <div style={{float:'left', width:'36%', border: '1px solid grey', padding: '10px', margin: '10px'}}>
        <Form>

          <Form.Group as={Row} controlId="formPlaintextSeatType">
          <Col sm="3">
            </Col>
          <Col sm="7">
            <Button variant="outline-primary">Import My Preference</Button>{' '}
            </Col>

          </Form.Group>


          <Form.Group as={Row} controlId="formPlaintextDistance">
