    var PowerHigh = this.props.posts;
    PowerHigh = PowerHigh.filter(x=>x.priority === "High" && x.problem === "Power");

    var MedicalHigh = this.props.posts;
    MedicalHigh = MedicalHigh.filter(x=>x.priority === "High" && x.problem === "Medical");

    var TotalHigh = this.props.posts;
    TotalHigh = FireHigh.length + FloodHigh.length + PowerHigh.length + MedicalHigh.length;

    //Critical
    var FireCritical = this.props.posts;
    FireCritical = FireCritical.filter(x=>x.priority === "Critical" && x.problem === "Fire");

    var FloodCritical = this.props.posts;
    FloodCritical = FloodCritical.filter(x=>x.priority === "Critical" && x.problem === "Flood");

    var PowerCritical = this.props.posts;
    PowerCritical = PowerCritical.filter(x=>x.priority === "Critical" && x.problem === "Power");

    var MedicalCritical = this.props.posts;
    MedicalCritical = MedicalCritical.filter(x=>x.priority === "Critical" && x.problem === "Medical");

    var TotalCritical = this.props.posts;
    TotalCritical = FireCritical.length + FloodCritical.length + PowerCritical.length + MedicalCritical.length;


    //total
    var TotalFire = FireLow.length + FireMedium.length + FireHigh.length + FireCritical.length;
    var TotalFlood = FloodLow.length + FloodMedium.length + FloodHigh.length + FloodCritical.length;
    var TotalPower = PowerLow.length + PowerMedium.length + PowerHigh.length + PowerCritical.length;
    var TotalMedical = MedicalLow.length + MedicalMedium.length + MedicalHigh.length + MedicalCritical.length;
    var TotalTotal = TotalFire + TotalFlood + TotalPower + TotalMedical;





    return(
    <div style={{padding: '50px'}}>
      <h2>Preference</h2><br/>
<Form>

  <Form.Group as={Row} controlId="formPlaintextSearch">
