import React, { Component } from 'react';
import './App.css';
import {
  HashRouter as Router,
  Route,
  NavLink,
  Redirect,
} from 'react-router-dom';

import { createHashHistory } from 'history';


import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Table, Image,ListGroup,ListGroupItem, CardGroup,Form, FormControl, FormCheck, Row, Col, Dropdown,DropdownButton,ToggleButton,ToggleButtonGroup} from 'react-bootstrap';

import Calendar from 'react-calendar';

import io from 'socket.io-client';
const socket = io('http://localhost:3001');
//
// See: https://socket.io/get-started/chat
//      https://www.npmjs.com/package/socket.io-client


const hashHistory = createHashHistory();


class Hpage extends React.Component{


  render(){
    return(
    <div style={{padding: '50px'}}>
    <h2>Homepage</h2><br/>
    <p>This is a dashboard for viewing and analyzing social media posts resulting from a disaster.</p>
    <p>Click the Live Feed button to see the live feed posts of the social media posts.</p>
    <p>Click the Analytics button to see the analytics result.</p>
    </div> 
    )}
}


class Page3 extends React.Component{

  constructor(props) {
    super(props);
    // this.state = { images: ["images/HSL.png","images/Thode.png","images/Mills.png"],date: new Date(),}

    this.state = {
    libraries: [
      {image: "images/HSL.png", 
       Group: false, 
       Distance:800,
        },
      {image: "images/Thode.png", 
        Group: true, 
        Distance:500,
         },
      {image: "images/Mills.png", 
         Group: true, 
         Distance:100,
        },
      // {image: "images/error.png", 
      //   Group: true, 
      //   Distance:0,
      //  }
    ],
      HasGroup:false, MaxDistance:1000}
  }


  onChange = date => this.setState({ date })

  filterGroup(){this.setState({HasGroup:!this.state.HasGroup})}

  // filterDistance(e){this.setState({HasDistance:e.target.getAttribute("value")})}
   handleChange =(e)=> {
     this.setState({MaxDistance:e.target.value});
     if(this.state.MaxDistance<=100) {this.setState({libraries:[
      {image: "images/error.png", 
       Group: true, 
       Distance:0,
        },
    ]})}
    if(this.state.MaxDistance>100) {this.setState({libraries:[
      {image: "images/HSL.png", 
       Group: false, 
       Distance:800,
        },
      {image: "images/Thode.png", 
        Group: true, 
        Distance:500,
         },
      {image: "images/Mills.png", 
         Group: true, 
         Distance:100,
        },
    ]})}



  }

    render(){

      var toDisplay = this.state.libraries;
      if(this.state.HasGroup)  toDisplay=  toDisplay.filter(x => x.Group == true);
      toDisplay = toDisplay.filter(x=>x.Distance<=this.state.MaxDistance);


      return(
      <div style={{padding: '50px'}}>
        <div style={{float:'left', width:'36%', borderRight: '1px solid grey', padding: '10px', margin: '10px'}}>
        <Form>

          <Form.Group as={Row} controlId="formPlaintextSeatType">
          <Col sm="3">
            </Col>
          <Col sm="7">
            <Button variant="outline-primary">Import My Preference</Button>{' '}
            </Col>

          </Form.Group>


          <Form.Group as={Row} controlId="formPlaintextDistance">
          <Form.Label column sm="5">
            DISTANCE
          </Form.Label>

          <Col sm="7">


          {/* <Form.Control type="range" min={100} max={5000} defaultValue={100}  onChange={()=>this.filterDistance()} /> */}
          <Form.Control type="range" min={0} max={1000} value={this.state.MaxDistance}  onChange={this.handleChange} />
          <Form.Label>0m</Form.Label>
          <Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label>500m<Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label>
          <Form.Label>1000m</Form.Label>
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formPlaintextSeatType">
          <Form.Label column sm="5">
            SEAT TYPE
          </Form.Label>
          <Col sm="7">
          <ToggleButtonGroup type="checkbox" defaultValue={[]} className="mb-2">
          <ToggleButton size = "sm" value={1} style={{margin:'2px'}}>PC </ToggleButton>
          <ToggleButton size = "sm" value={2} onChange={() => this.filterGroup()}style={{margin:'2px'}}>Group</ToggleButton>
          <ToggleButton size = "sm" value={3} style={{margin:'2px'}}>Printer</ToggleButton>
          <ToggleButton size = "sm" value={4} style={{margin:'2px'}}>Food </ToggleButton>
          <ToggleButton size = "sm" value={5} style={{margin:'2px'}}>Quiet</ToggleButton>
        </ToggleButtonGroup>

          </Col>
        </Form.Group>


        <Form.Group as={Row} controlId="formPlaintextSeatType">
          <Form.Label column sm="5">
            SEAT TYPE
          </Form.Label>
          <Col sm="9"style={{margin: '30px'}}>
          <Calendar
          onChange={this.onChange}
          value={this.state.date}
        />
          </Col>
        </Form.Group>

        </Form>
      <span>{this.props.location.state.sdistance}</span><span>1</span><span>1</span><span>1</span> 
        </div>





        <div style={{float:'left', width:'61%', height:'550px',border: '0px solid grey', padding: '10px', margin: '10px'}}>
        { toDisplay.map( 
            ({image,Group}) => 
            <img width = "90%" src={image}/>
             )}
        </div>
      </div> 
      )}
}

class Page1 extends React.Component{

  constructor(props){
    super(props)
    this.state={low: true, medium: true, high: true, critical:true, fire:true, flood:true, power:true, medical:true};
  }

  filterLow(){this.setState({low:!this.state.low});}
  filterMedium(){this.setState({medium:!this.state.medium});}
  filterHigh(){this.setState({high:!this.state.high});}
  filterCritical(){this.setState({critical:!this.state.critical});}
  filterFire(){this.setState({fire:!this.state.fire});}
  filterFlood(){this.setState({flood:!this.state.flood});}
  filterPower(){this.setState({power:!this.state.power});}
  filterMedical(){this.setState({medical:!this.state.medical});}


  render(){

    var toshow = this.props.posts;
    if(!this.state.low) toshow = toshow.filter(x => x.priority !== "Low");
    if(!this.state.medium) toshow = toshow.filter(x => x.priority !== "Medium");
    if(!this.state.high) toshow = toshow.filter(x => x.priority !== "High");
    if(!this.state.critical) toshow = toshow.filter(x => x.priority !== "Critical");
    if(!this.state.fire) toshow = toshow.filter(x => x.problem !== "Fire");
    if(!this.state.flood) toshow = toshow.filter(x => x.problem !== "Flood");
    if(!this.state.power) toshow = toshow.filter(x => x.problem !== "Power");
    if(!this.state.medical) toshow = toshow.filter(x => x.problem !== "Medical");



    return(
      <div style={{padding: '40px'}}>
        <h2>Live Feed</h2>
        <ul class="list-group" id='fixed'>
        <li class="list-group-item" style={{fontSize:'20px'}}> 
            <input type = "checkbox" id="low" checked={this.state.low} onChange={this.filterLow.bind(this)} />
            <label for="low">Low</label>
        </li>
        <li class="list-group-item" style={{fontSize:'20px'}}>
            <input type = "checkbox" id="medium" checked={this.state.medium}
                onChange={this.filterMedium.bind(this)}/>
            <label for="medium">Medium</label>
        </li>
        <li class="list-group-item" style={{fontSize:'20px'}}>
        <input type = "checkbox" id="high" checked={this.state.high}
            onChange={this.filterHigh.bind(this)}/>
        <label for="high">High</label>
        </li>

        <li class="list-group-item" style={{fontSize:'20px'}}>
        <input type = "checkbox" id="critical" checked={this.state.critical}
            onChange={this.filterCritical.bind(this)}/>
        <label for="critical">Critical</label>     
        </li>

        <li class="list-group-item" style={{fontSize:'20px'}}>
        <input type = "checkbox" id="fire" checked={this.state.fire}
            onChange={this.filterFire.bind(this)}/>
        <label for="fire">Fire</label>
        </li>



        <li class="list-group-item" style={{fontSize:'20px'}}> 
        <input type = "checkbox" id="flood" checked={this.state.flood}
            onChange={this.filterFlood.bind(this)}/>
        <label for="flood">Flood</label>
        </li>


        <li class="list-group-item" style={{fontSize:'20px'}}>
        <input type = "checkbox" id="power" checked={this.state.power}
            onChange={this.filterPower.bind(this)}/>
        <label for="power">Power</label>
        </li>

        <li class="list-group-item" style={{fontSize:'20px'}}>
        <input type = "checkbox" id="medical" checked={this.state.medical}
            onChange={this.filterMedical.bind(this)}/>
        <label for="medical">Medical</label>
        </li>

        </ul>

        {/* <div>
          {toshow.map(x=><div>{x.priority}</div>)}
          {this.props.posts.map(({priority})=><div>{priority}</div>)}
        </div> */}




        <div style={{float:'left',width:'80%',backgroundColor:'#f9f9f9'}}>
           {toshow.map( 
              ({name,image,content,problem,priority,id}) => 
              <div style={{float:'left', width:'100%', border: '0px solid grey', padding: '30px'}}
                key={id}>
                  <Image style={{float:'left', width:'15%',marginTop:'15px'}} src={image} alt="{name}" width="200px" roundedCircle />
                  {/* <div border = "1px" >
                  {name} <br />
                  {problem} <br />
                  {priority} <br />
                  {content} <br /> <br /> <br /> <br />
                  </div> */}
                  <ListGroup>
                  <ListGroup.Item>Name: {name}</ListGroup.Item>
                  <ListGroup.Item>Problem: {problem}</ListGroup.Item>
                  <ListGroup.Item> Priority: {priority} </ListGroup.Item>
                  <ListGroup.Item>{content}</ListGroup.Item>
                </ListGroup>
              </div> 
            )} 

        </div>
      </div>
    )}
}

class Page2 extends React.Component{

  constructor(props){
    super(props)
    this.state={distance:0, timeslot:"Pick a time slot", pc:true, group:false, printer:false, food:false, quiet:false};
  }
  SetSlot1(){this.setState({timeslot:"8.00am-11.00am"})}
  SetSlot2(){this.setState({timeslot:"1.00pm-5.00pm"})}
  SetSlot3(){this.setState({timeslot:"5.00pm-8.00pm"})}
  FlipPc(){this.setState({pc:!this.state.pc})}
  FlipGroup(){this.setState({group:!this.state.group})}
  FlipPrinter(){this.setState({printer:!this.state.printer})}
  FlipFood(){this.setState({food:!this.state.food})}
  FlipQuiet(){this.setState({quiet:!this.state.quiet})}
  SubmitPreferene(){
    hashHistory.push({
      pathname: '/browse',
      query: {
       sdistance: this.state.distance
      },
    })
}


  render(){




    //Low
    var FireLow = this.props.posts;
    FireLow = FireLow.filter(x=>x.priority === "Low" && x.problem === "Fire");

    var FloodLow = this.props.posts;
    FloodLow = FloodLow.filter(x=>x.priority === "Low" && x.problem === "Flood");

    var PowerLow = this.props.posts;
    PowerLow = PowerLow.filter(x=>x.priority === "Low" && x.problem === "Power");

    var MedicalLow = this.props.posts;
    MedicalLow = MedicalLow.filter(x=>x.priority === "Low" && x.problem === "Medical");

    var TotalLow = this.props.posts;
    TotalLow = FireLow.length + FloodLow.length + PowerLow.length + MedicalLow.length;

    //Medium
    var FireMedium = this.props.posts;
    FireMedium = FireMedium.filter(x=>x.priority === "Medium" && x.problem === "Fire");

    var FloodMedium = this.props.posts;
    FloodMedium = FloodMedium.filter(x=>x.priority === "Medium" && x.problem === "Flood");

    var PowerMedium = this.props.posts;
    PowerMedium = PowerMedium.filter(x=>x.priority === "Medium" && x.problem === "Power");

    var MedicalMedium = this.props.posts;
    MedicalMedium = MedicalMedium.filter(x=>x.priority === "Medium" && x.problem === "Medical");

    var TotalMedium = this.props.posts;
    TotalMedium = FireMedium.length + FloodMedium.length + PowerMedium.length + MedicalMedium.length;


    //High
    var FireHigh = this.props.posts;
    FireHigh = FireHigh.filter(x=>x.priority === "High" && x.problem === "Fire");

    var FloodHigh = this.props.posts;
    FloodHigh = FloodHigh.filter(x=>x.priority === "High" && x.problem === "Flood");

    var PowerHigh = this.props.posts;
    PowerHigh = PowerHigh.filter(x=>x.priority === "High" && x.problem === "Power");

    var MedicalHigh = this.props.posts;
    MedicalHigh = MedicalHigh.filter(x=>x.priority === "High" && x.problem === "Medical");

    var TotalHigh = this.props.posts;
    TotalHigh = FireHigh.length + FloodHigh.length + PowerHigh.length + MedicalHigh.length;

    //Critical
    var FireCritical = this.props.posts;
    FireCritical = FireCritical.filter(x=>x.priority === "Critical" && x.problem === "Fire");

    var FloodCritical = this.props.posts;
    FloodCritical = FloodCritical.filter(x=>x.priority === "Critical" && x.problem === "Flood");

    var PowerCritical = this.props.posts;
    PowerCritical = PowerCritical.filter(x=>x.priority === "Critical" && x.problem === "Power");

    var MedicalCritical = this.props.posts;
    MedicalCritical = MedicalCritical.filter(x=>x.priority === "Critical" && x.problem === "Medical");

    var TotalCritical = this.props.posts;
    TotalCritical = FireCritical.length + FloodCritical.length + PowerCritical.length + MedicalCritical.length;


    //total
    var TotalFire = FireLow.length + FireMedium.length + FireHigh.length + FireCritical.length;
    var TotalFlood = FloodLow.length + FloodMedium.length + FloodHigh.length + FloodCritical.length;
    var TotalPower = PowerLow.length + PowerMedium.length + PowerHigh.length + PowerCritical.length;
    var TotalMedical = MedicalLow.length + MedicalMedium.length + MedicalHigh.length + MedicalCritical.length;
    var TotalTotal = TotalFire + TotalFlood + TotalPower + TotalMedical;





    return(
    <div style={{padding: '50px'}}>
      <h2>Preference</h2><br/>
<Form>

  <Form.Group as={Row} controlId="formPlaintextSearch">
    <Form.Label column sm="2">
      School
    </Form.Label>
    <Col sm="5">
      <Form.Control type="text" placeholder="Search" />
    </Col>
  </Form.Group>


  <Form.Group as={Row} controlId="formPlaintextAddress">
    <Form.Label column sm="2">
      Address
    </Form.Label>
    <Col sm="5">
      <Form.Control type="text" placeholder="" />
    </Col>
  </Form.Group>

  <Form.Group as={Row} controlId="formPlaintextDistance">
    <Form.Label column sm="2">
      Prefered Distance
    </Form.Label>
    <Col sm="5">
    <Form.Control type="range" />
    <Form.Label>100m</Form.Label>
    <Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label><Form.Label></Form.Label>
    <Form.Label>1000m</Form.Label>
    </Col>
  </Form.Group>


  <Form.Group as={Row} controlId="formPlaintextTimeSlots">
    <Form.Label column sm="2">
      Prefered Time Slots
    </Form.Label>
    <Col sm="5">

  <DropdownButton id="dropdown-basic-button" title={this.state.timeslot}>
    <Dropdown.Item onClick={() => this.SetSlot1()}>8.00am-11.00am</Dropdown.Item>
    <Dropdown.Item onClick={() => this.SetSlot2()}>1.00pm-5.00pm</Dropdown.Item>
    <Dropdown.Item onClick={() => this.SetSlot3()}>5.00pm-8.00pm</Dropdown.Item>
  </DropdownButton>
    </Col>
  </Form.Group>

  <Form.Group as={Row} controlId="formPlaintextSeatType">
    <Form.Label column sm="2">
      Prefered Seat Type
    </Form.Label>
